<?php if($_POST){$name="";$email="";$message="";$email_body="<div>";if(isset($_POST['name'])){$name=filter_var($_POST['name'],FILTER_SANITIZE_STRING);$email_body.="<div>\n                           <label><b>Visitor Name:</b></label>&nbsp;<span>".$name."</span>\n                        </div>";}if(isset($_POST['email'])){$email=str_replace(array("\r","\n","%0a","%0d"),'',$_POST['email']);$email=filter_var($email,FILTER_VALIDATE_EMAIL);$email_body.="<div>\n                           <label><b>Visitor Email:</b></label>&nbsp;<span>".$email."</span>\n                        </div>";}if(isset($_POST['message'])){$message=htmlspecialchars($_POST['message']);$email_body.="<div>\n                           <label><b>Visitor Message:</b></label>\n                           <div>".$message."</div>\n                        </div>";}$recipient="milan.m.dimitrijevic@gmail.com";$email_body.="</div>";$headers='MIME-Version: 1.0'."\r\n".'Content-type: text/html; charset=utf-8'."\r\n".'From: '.$email."\r\n";if(mail($recipient,$email_body,$headers)){echo"<p>Thank you for contacting us, $name. You will get a reply within 24 hours.</p>";}else{echo '<p>We are sorry but the email did not go through. <br />Please try again or send an email directly to milan.m.dimitrijevic@gmail.com</p>';}}else{echo '<p>UPS, Something went wrong! <br />Please try again or send an email directly to milan.m.dimitrijevic@gmail.com</p>';} ?>